# Mean and standard deviation of fresh mass per line and sex
grouped <- group_by(datos, stock,sex)
datos.mean<-summarise(grouped, mean=mean(fresh.mass), sd=sd(fresh.mass))

round_df <- function(datos.mean, digits) {
  nums <- vapply(datos.mean, is.numeric, FUN.VALUE = logical(1))
  datos.mean[,nums] <- round(datos.mean[,nums], digits = digits)
  
  (datos.mean)
}

datos.mean<-round_df(datos.mean, digits=3)

# Table S2
write.csv(datos.mean,"3.1.1. Table S2 Mean and SD of fresh mass by stock and sex.csv",row.names = FALSE)

