  box()
  boxplot(cell.number~sex,data=datos,col=c('#4DAC2660', '#A6761D60'),boxwex=0.5,xaxt='n',cex.axis=0.6,las=1,
          ylab="Cell number")
  legend("topleft",legend = "(d)",bty = "n")
  box()
  boxplot(cell.area~sex,data=datos,col=c('#4DAC2660', '#A6761D60'),boxwex=0.5,xaxt='n',axt='n',cex.axis=0.6,las=1,
          ylab=expression(paste("Cell area ","(", mu,"m"^"2",")")))
  legend("topleft",legend = "(e)",bty = "n")
  box()
  axis(1,at=c(1,2),labels=c("Females","Males"))
  
  dev.off()
}
